mapper.select:
SELECT nts.id, nts.code, nts.name, nts.description, nts.fd_level, nts.pm_type, nts.retry_count, nts.is_send_instantly, nts.is_manual_retry, nts.is_allow_config, nts.is_enabled, nts.object_version_number, ntemail.id AS email_template_id, ntemail.code AS email_template_code, ntsms.id AS sms_template_id, ntsms.code AS sms_template_code, ntpm.id AS pm_template_id, ntpm.code AS pm_template_code FROM notify_send_setting nts LEFT JOIN notify_template ntemail on nts.email_template_id = ntemail.id LEFT JOIN notify_template ntsms on nts.sms_template_id = ntsms.id LEFT JOIN notify_template ntpm on nts.pm_template_id = ntpm.id where nts.is_enabled = 1
AND nts.fd_level = #{level}
AND nts.is_allow_config = #{allowConfig}

SELECT id, code, NAME AS message_type, description AS introduce, fd_level AS LEVEL, is_enabled as enabled, is_allow_config AS allowConfig, object_version_number FROM notify_send_setting WHERE 1 = 1
AND fd_level = #{level}
AND name LIKE concat(concat('%',#{messageType}),'%')
AND description LIKE concat(concat('%',#{introduce}),'%')
AND is_enabled = #{enabled}
AND is_allow_config = #{allowConfig}
AND( name LIKE concat(concat('%',#{params}),'%') OR description LIKE concat(concat('%',#{params}),'%') )
